
number = (
    ""
)


def max_product_in_series(number, length):
    max_product = 0
    for i in range(len(number) - length + 1):
        digits = number[i:i + length]
        product = 1
        for digit in digits:
            product *= int(digit)
        max_product = max(max_product, product)
    return max_product


result = max_product_in_series(number, 13)
print("The greatest product of 13 adjacent digits is:", result)
